mapper.select:
select count(*) cnt
from  PRODUCT p, MEMBER m,CATEGORY2 c2,CATEGORY3 c3
where m.id=p.id   and p.C3CODE = c3.C3CODE and c2.C2CODE=c3.C2CODE

	
		
		and category like #{ keyword }
		
		
		and nickname like #{ keyword }

select NICKNAME , PCODE,NAME as category, PNAME,PRICE,INPUT_DATE
from(select m.NICKNAME,p.PCODE, c2.NAME, p.PNAME, p.PRICE, p.INPUT_DATE
			,row_number() over(order by p.INPUT_DATE desc) rnum
from  PRODUCT p, MEMBER m,CATEGORY2 c2,CATEGORY3 c3
where m.id=p.id   and p.C3CODE = c3.C3CODE and c2.C2CODE=c3.C2CODE 
		
			
			and category like #{ keyword }
			
			
			and nickname like #{ keyword }
			
		
)
 where rnum between 1 and 10

mapper.update:
update PRODUCT
set   PCANCEL=#{pcancel}
where  PCODE=#{pcode}
