mapper.select:
SELECT ID, NICKNAME, EMAIL, PHONE
FROM MEMBER
WHERE ID=#{id} AND PW=#{pw}

mapper.update:
UPDATE MEMBER
SET PW=#{newPw}
WHERE ID=#{id} and PW=#{oldPw}

UPDATE MEMBER
SET STATUS='C'
WHERE ID=#{id}

mapper.select:
SELECT PCODE, PNAME, PRICE, INPUT_DATE, LOC
FROM PRODUCT
WHERE ID=#{id}
