mapper.select:
select m.nickname as buyerNick, c.BUYER_ID as buyerId
from CHATTING c, member m
where m.id = c.BUYER_ID and c.SELLER_ID = #{ seller }

select count(*) cnt
from wish
where pcode=#{ pcode }

mapper.update:
update PRODUCT
set  
		
 		pname=#{pname}
 		
 		
 		,context=#{context}
 		
 		
 		,img=#{img}
 		
		
 		,img2=#{img2}
 		
 		
 		,img3=#{img3}
 		
 		
 		,img4=#{img4}
 		
 		
 		,img5=#{img5}
 		
 		
 		,price=#{price}
 		
 		
 		,deliver=#{deliver}
 		
 		
 		,status=#{status}
 		
		
 		,loc=#{loc}
 		
 		
 		,c3code=#{c3code}
 		
where PCODE=#{pcode}

update PRODUCT
set saleok='Y'
where PCODE=#{pcode}
